import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Navbar, Container, Row, Col, Button, Form, Modal } from 'react-bootstrap';
import './styles.css'; // Import CSS file

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showEditProduct, setShowEditProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });
  const [editingProduct, setEditingProduct] = useState({
    id: null,
    productName: '',
    price: '',
    linkImage: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (showEditProduct) {
      setEditingProduct({ ...editingProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowAddProduct(false);
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleToggleAddProduct = () => {
    setShowAddProduct(!showAddProduct);
    setNewProduct({
      productName: '',
      price: '',
      linkImage: '',
    });
  };

  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit);
    setShowEditProduct(true);
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8082/api/products/${editingProduct.id}`, editingProduct);
      setEditingProduct({
        id: null,
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowEditProduct(false);
    } catch (error) {
      console.error('Error editing product:', error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8082/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <>
      <Navbar bg="dark" variant="dark">
        <Container>
          <Navbar.Brand href="#">Product Management</Navbar.Brand>
        </Container>
      </Navbar>

      <Container className="mt-3">
        <Row className="mb-3">
          <Col>
            <Button onClick={handleToggleAddProduct} variant="primary">
              {showAddProduct ? 'Close Form' : 'Add Product'}
            </Button>
          </Col>
        </Row>

        {showAddProduct && (
          <Row>
            <Col>
              <Form onSubmit={handleSubmit}>
                <Form.Group controlId="productName">
                  <Form.Label>Product Name</Form.Label>
                  <Form.Control type="text" name="productName" value={newProduct.productName} onChange={handleInputChange} />
                </Form.Group>
                <Form.Group controlId="price">
                  <Form.Label>Price</Form.Label>
                  <Form.Control type="number" name="price" value={newProduct.price} onChange={handleInputChange} />
                </Form.Group>
                <Form.Group controlId="linkImage">
                  <Form.Label>Image URL</Form.Label>
                  <Form.Control type="text" name="linkImage" value={newProduct.linkImage} onChange={handleInputChange} />
                </Form.Group>
                <Button type="submit" variant="success">Add Product</Button>
              </Form>
            </Col>
          </Row>
        )}

        <Row>
          {products.map(product => (
            <Col key={product.id} md={4}>
              <div className="product-card mb-3 p-3">
                <img src={product.linkImage} alt={product.productName} className="img-fluid mb-2" />
                <h4>{product.productName}</h4>
                <p>Price: ${product.price}</p>
                <Button onClick={() => handleEditProduct(product.id)} variant="primary" className="mr-2">Edit</Button>
                <Button onClick={() => handleDeleteProduct(product.id)} variant="danger">Delete</Button>
              </div>
            </Col>
          ))}
        </Row>
      </Container>

      <Modal show={showEditProduct} onHide={() => setShowEditProduct(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="editProductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control type="text" name="productName" value={editingProduct.productName} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="editPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" name="price" value={editingProduct.price} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="editLinkImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" name="linkImage" value={editingProduct.linkImage} onChange={handleInputChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditProduct(false)}>Close</Button>
          <Button variant="primary" onClick={handleSaveEdit}>Save Changes</Button>
        </Modal.Footer>
      </Modal>
    </>
  );
}

export default ProductList;
