// ProductList.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import CSS file

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
    quantity: ''
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8080/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
        quantity: ''
      });
      setShowAddProduct(false); // Hide add product form
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <div>
      <button onClick={() => setShowAddProduct(true)}>Add Product</button>

      {showAddProduct && (
        <div>
          <h2>Add Product</h2>
          <form onSubmit={handleSubmit}>
            <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
            <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
            <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
            <input type="number" name="quantity" value={newProduct.quantity} placeholder="Quantity" onChange={handleInputChange} />
            <button type="submit">Add Product</button>
          </form>
        </div>
      )}

      <table className="product-table">
        <thead>
          <tr>
            <th>#</th>
            <th>Name</th>
            <th>Price</th>
            <th>Image</th>
            <th>Quantity</th>
          </tr>
        </thead>
        <tbody>
          {products.map((product, index) => (
            <tr key={product.id}>
              <td>{index + 1}</td>
              <td>{product.productName}</td>
              <td>{product.price}</td>
              <td><img src={product.linkImage} alt={product.productName} /></td>
              <td>{product.quantity}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ProductList;
