import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import CSS file
import { Modal, Button, Form } from 'react-bootstrap'; // Import components from react-bootstrap


function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
    quantity: '',
  });
  const [editingProduct, setEditingProduct] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false); // State to control modal visibility
  const [editedProduct, setEditedProduct] = useState({
    id: null,
    productName: '',
    price: '',
    linkImage: '',
    quantity: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setEditedProduct({ ...editedProduct, [name]: value });
  };

  const handleToggleAddProduct = () => {
    setShowAddProduct(!showAddProduct);
    setNewProduct({
      productName: '',
      price: '',
      linkImage: '',
      quantity: '',
    });
  };

  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit);
    setEditedProduct(productToEdit);
    setShowEditModal(true); // Show edit modal
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8082/api/products/${editedProduct.id}`, editedProduct);
      setProducts(products.map(product =>
        product.id === editedProduct.id ? editedProduct : product
      ));
      setShowEditModal(false); // Hide edit modal after saving
    } catch (error) {
      console.error('Error saving edit:', error);
    }
  };

  const handleCancelEdit = () => {
    setShowEditModal(false); // Hide edit modal
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8082/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <button onClick={handleToggleAddProduct}>
        {showAddProduct ? 'Close Form' : 'Add Product'}
      </button>

      <Modal show={showEditModal} onHide={handleCancelEdit}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control type="text" name="productName" value={editedProduct.productName} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="price">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" name="price" value={editedProduct.price} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="linkImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" name="linkImage" value={editedProduct.linkImage} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group controlId="quantity">
              <Form.Label>Quantity</Form.Label>
              <Form.Control type="number" name="quantity" value={editedProduct.quantity} onChange={handleInputChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleCancelEdit}>
            Cancel
          </Button>
          <Button variant="primary" onClick={handleSaveEdit}>
            Save
          </Button>
        </Modal.Footer>
      </Modal>

      {/* Rest of your code for adding new products and displaying existing products */}
    </div>
  );
}

export default ProductList;
