import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './ProductList.css'; // Import CSS file
import { Modal, Button, Form } from 'react-bootstrap';
import { Card, Container, Row, Col } from "react-bootstrap";
import { IoAddCircle } from "react-icons/io5";
import { FaPlus } from 'react-icons/fa'; // Import FaPlus icon

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showEditProduct, setShowEditProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });
  const [editingProduct, setEditingProduct] = useState({
    id: null,
    productName: '',
    price: '',
    linkImage: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8085/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (showEditProduct) {
      setEditingProduct({ ...editingProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (showEditProduct) {
      handleSaveEdit();
    } else {
      try {
        const response = await axios.post('http://localhost:8085/api/products', newProduct); // Sử dụng axios.post để thêm sản phẩm mới
        setProducts([...products, response.data]);
        setNewProduct({
          productName: '',
          price: '',
          linkImage: '',
        });
        setShowAddProduct(false); // Close the modal after adding product
      } catch (error) {
        console.error('Error adding product:', error);
      }
    }
  };


  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit);
    setShowEditProduct(true);
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8085/api/products/${editingProduct.id}`, editingProduct);
      const updatedProducts = products.map(product =>
        product.id === editingProduct.id ? editingProduct : product
      );
      setProducts(updatedProducts);
      setEditingProduct({
        id: null,
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowEditProduct(false);
    } catch (error) {
      console.error('Error editing product:', error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8085/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <h1 
        style={{ color: 'white',
                 textAlign: 'center', 
                 marginTop: '20px' 
                }} 
      >
        Product Management
      </h1>

      <Modal
        size="lg"
        show={showAddProduct}
        onHide={() => setShowAddProduct(false)}
        aria-labelledby="contained-modal-title-vcenter"
        centered
        className='custom-modal'
      >
        <Modal.Body className="modal-body">
          <div className="container">
            <div className="form-container">
              <h2>Add Product</h2>
              <form onSubmit={handleSubmit}>
                <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
                <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
                <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
                <div className='btn-form'>
                  <button type="submit">Add Product</button>
                  <button type="button" onClick={() => setShowAddProduct(false)}>Cancel</button>
                </div>
              </form>
            </div>
          </div>
        </Modal.Body>
      </Modal>

      <Modal
        size="lg"
        show={showEditProduct}
        onHide={() => setShowEditProduct(false)}
        aria-labelledby="contained-modal-title-vcenter"
        centered
        className='custom-modal'
      >
        <Modal.Body className="modal-body">
          <div className="container">
            <div className="form-container">
              <h2>Edit Product</h2>
              <form onSubmit={handleSubmit}>
                <input type="text" name="productName" value={editingProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
                <input type="number" name="price" value={editingProduct.price} placeholder="Price" onChange={handleInputChange} />
                <input type="text" name="linkImage" value={editingProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
                <div className='btn-form'>
                  <button type="submit">Save</button>
                  <button type="button" onClick={() => setShowEditProduct(false)}>Cancel</button>
                </div>
              </form>
            </div>
          </div>
        </Modal.Body>
      </Modal>

      <div className="container">
        <div className="table-container">
          {products.map((product, index) => (
            <div className="product-card" key={product.id}>
              <div className='body-card'> 
                <img src={product.linkImage} alt={product.productName} />
                <h3>{product.productName}</h3>
                <p>Giá: {product.price}K</p>
              </div>
              <div className='btn-card'>
                <button className='btn-edit' onClick={() => handleEditProduct(product.id)}>Edit</button>
                <button className='btn-delete' onClick={() => handleDeleteProduct(product.id)}>Delete</button>
              </div>
            </div>
          ))}
          <div className="product-card-1 product-card" onClick={() => setShowAddProduct(true)}>
            <div className='card-btn-add' >            
              <FaPlus className="btn-add" />
            </div>
          </div>
        </div>
      </div>


    </div>
  );
}

export default ProductList;
