import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import './styles.css'; // Import CSS file

function ProductList() {
  const [showModal, setShowModal] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowModal(false);
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  return (
    <div>
      <Button onClick={() => setShowModal(true)}>Add Product</Button>

      <Modal
        size="lg"
        show={showModal}
        onHide={() => setShowModal(false)}
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Header closeButton>
          <Modal.Title id="contained-modal-title-vcenter">
            Add Product
          </Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form onSubmit={handleSubmit}>
            <Form.Group className="mb-3" controlId="productName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control type="text" name="productName" value={newProduct.productName} onChange={handleInputChange} autoFocus />
            </Form.Group>
            <Form.Group className="mb-3" controlId="price">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" name="price" value={newProduct.price} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3" controlId="linkImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" name="linkImage" value={newProduct.linkImage} onChange={handleInputChange} />
            </Form.Group>
            <Button type="submit">Submit</Button>
          </Form>
        </Modal.Body>
      </Modal>

      {/* Product list rendering code here */}
    </div>
  );
}

export default ProductList;
