import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, TextField } from '@mui/material';

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showEditProduct, setShowEditProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });
  const [editingProduct, setEditingProduct] = useState({
    id: null,
    productName: '',
    price: '',
    linkImage: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (showEditProduct) {
      setEditingProduct({ ...editingProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowAddProduct(false);
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleToggleAddProduct = () => {
    setShowAddProduct(!showAddProduct);
    setNewProduct({
      productName: '',
      price: '',
      linkImage: '',
    });
  };

  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit);
    setShowEditProduct(true);
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8082/api/products/${editingProduct.id}`, editingProduct);
      setEditingProduct({
        id: null,
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowEditProduct(false);
    } catch (error) {
      console.error('Error editing product:', error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8082/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <Button variant='contained' color='primary' onClick={handleToggleAddProduct}>
        {showAddProduct ? 'Close Form' : 'Add Product'}
      </Button>

      {showAddProduct && (
        <div className="container">
          <div className="form-container">
            <h2>Add Product</h2>
            <form onSubmit={handleSubmit}>
              <TextField type="text" name="productName" value={newProduct.productName} label="Product Name" onChange={handleInputChange} />
              <TextField type="number" name="price" value={newProduct.price} label="Price" onChange={handleInputChange} />
              <TextField type="text" name="linkImage" value={newProduct.linkImage} label="Image URL" onChange={handleInputChange} />
              <Button type="submit" variant="contained" color="primary">Add Product</Button>
            </form>
          </div>
        </div>
      )}

      <div className="container">
        <div className="table-container">
          {products.map((product, index) => (
            <div className="product-card" key={product.id}>
              <img src={product.linkImage} alt={product.productName} />
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              <div className='btn-card'>
                <Button variant="contained" color="primary" onClick={() => handleEditProduct(product.id)}>Edit</Button>
                <Button variant="contained" color="secondary" onClick={() => handleDeleteProduct(product.id)}>Delete</Button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <Modal open={showEditProduct} onClose={() => setShowEditProduct(false)}>
        <div style={{ padding: 16 }}>
          <h2>Edit Product</h2>
          <form>
            <div className="mb-3">
              <TextField type="text" name="productName" value={editingProduct.productName} label="Product Name" onChange={handleInputChange} />
            </div>
            <div className="mb-3">
              <TextField type="number" name="price" value={editingProduct.price} label="Price" onChange={handleInputChange} />
            </div>
            <div className="mb-3">
              <TextField type="text" name="linkImage" value={editingProduct.linkImage} label="Image URL" onChange={handleInputChange} />
            </div>
          </form>
          <Button variant="contained" onClick={() => setShowEditProduct(false)}>Close</Button>
          <Button variant="contained" color="primary" onClick={handleSaveEdit}>Save Changes</Button>
        </div>
      </Modal>
    </div>
  );
}

export default ProductList;
