import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import CSS file
import Modal from 'react-bootstrap/Modal';
import Button from 'react-bootstrap/Button';

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showEditProduct, setShowEditProduct] = useState(false); // Thêm state để hiển thị modal chỉnh sửa
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });
  const [editingProduct, setEditingProduct] = useState(null); // Thêm state mới

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
      });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleToggleAddProduct = () => {
    setShowAddProduct(!showAddProduct); // Toggle the state
    setNewProduct({
      productName: '',
      price: '',
      linkImage: '',
    });
  };

  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit); // Lưu thông tin sản phẩm đang chỉnh sửa vào state mới
    setShowEditProduct(true); // Hiển thị modal chỉnh sửa
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8082/api/products/${editingProduct.id}`, editingProduct);
      setEditingProduct(null); // Đặt lại state chỉnh sửa về null sau khi lưu
      setShowEditProduct(false); // Ẩn modal chỉnh sửa
    } catch (error) {
      console.error('Error editing product:', error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8082/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <button className='btn-add' onClick={handleToggleAddProduct}>
        {showAddProduct ? 'Close Form' : 'Add Product'}
      </button>

      {showAddProduct && (
        <div className="container">
          <div className="form-container">
            <h2>Add Product</h2>
            <form onSubmit={handleSubmit}>
              <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
              <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
              <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
              <button type="submit">Add Product</button>
            </form>
          </div>
        </div>
      )}

      <div className="container">
        <div className="table-container">
          {products.map((product, index) => (
            <div className="product-card" key={product.id}>
              <img src={product.linkImage} alt={product.productName} />
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              <div className='btn-card'>
                <button className='btn-edit' onClick={() => handleEditProduct(product.id)}>Edit</button>
                <button className='btn-delete' onClick={() => handleDeleteProduct(product.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Modal chỉnh sửa sản phẩm */}
      <Modal show={showEditProduct} onHide={() => setShowEditProduct(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <form>
            <div className="mb-3">
              <label htmlFor="editProductName" className="form-label">Product Name</label>
              <input type="text" className="form-control" id="editProductName" name="productName" value={editingProduct?.productName} onChange={handleInputChange} />
            </div>
            <div className="mb-3">
              <label htmlFor="editPrice" className="form-label">Price</label>
              <input type="number" className="form-control" id="editPrice" name="price" value={editingProduct?.price} onChange={handleInputChange} />
            </div>
            <div className="mb-3">
              <label htmlFor="editLinkImage" className="form-label">Image URL</label>
              <input type="text" className="form-control" id="editLinkImage" name="linkImage" value={editingProduct?.linkImage} onChange={handleInputChange} />
            </div>
          </form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditProduct(false)}>Close</Button>
          <Button variant="primary" onClick={handleSaveEdit}>Save Changes</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProductList;
