import React, { useState, useEffect, useRef } from 'react';
import axios from 'axios';
import tippy from 'tippy.js'; // Import tippy library
import 'tippy.js/dist/tippy.css'; // Import tippy styles
import './styles.css'; // Import CSS file

function ProductList() {
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
    quantity: ''
  });
  const formRef = useRef(null);

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8080/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8080/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
        quantity: ''
      });
      formRef.current._tippy.hide(); // Hide the tippy tooltip after submitting
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  useEffect(() => {
    // Initialize tippy tooltip for the form
    tippy(formRef.current, {
      content: formRef.current,
      trigger: 'click',
      interactive: true,
      arrow: true,
    });
  }, []);

  return (
    <div>
      <button ref={formRef}>Add Product</button>
      <div className="form-container" ref={formRef}>
        <h2>Add Product</h2>
        <form onSubmit={handleSubmit}>
          <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
          <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
          <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
          <input type="number" name="quantity" value={newProduct.quantity} placeholder="Quantity" onChange={handleInputChange} />
          <button type="submit">Add Product</button>
        </form>
      </div>

      <div className="container">
        <div className="table-container">
          {products.map((product, index) => (
            <div className="product-card" key={product.id}>
              <img src={product.linkImage} alt={product.productName} />
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              <p>Quantity: {product.quantity}</p>
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProductList;
