import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './styles.css'; // Import CSS file

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
    quantity: '', // Add quantity field
  });
  const [editingProduct, setEditingProduct] = useState(null); // State for editing product

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setNewProduct({ ...newProduct, [name]: value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
        quantity: '', // Reset quantity field after submission
      });
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleToggleAddProduct = () => {
    setShowAddProduct(!showAddProduct);
    setNewProduct({
      productName: '',
      price: '',
      linkImage: '',
      quantity: '', // Reset quantity field when toggling
    });
  };

  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit); // Set editingProduct state to the product being edited
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8082/api/products/${editingProduct.id}`, editingProduct);
      // Update products state with edited product
      setProducts(products.map(product =>
        product.id === editingProduct.id ? editingProduct : product
      ));
      setEditingProduct(null); // Clear editingProduct state after saving edit
    } catch (error) {
      console.error('Error saving edit:', error);
    }
  };

  const handleCancelEdit = () => {
    setEditingProduct(null); // Clear editingProduct state
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8082/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <button onClick={handleToggleAddProduct}>
        {showAddProduct ? 'Close Form' : 'Add Product'}
      </button>

      {showAddProduct && (
        <div className="container">
          <div className="form-container">
            <h2>Add Product</h2>
            <form onSubmit={handleSubmit}>
              <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
              <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
              <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
              <input type="number" name="quantity" value={newProduct.quantity} placeholder="Quantity" onChange={handleInputChange} />
              <button type="submit">Add Product</button>
            </form>
          </div>
        </div>
      )}

      <div className="container">
        <div className="table-container">
          {products.map((product, index) => (
            <div className="product-card" key={product.id}>
              <img src={product.linkImage} alt={product.productName} />
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              {editingProduct && editingProduct.id === product.id ? (
                <div className="btn-card">
                  <button className='btn-save' onClick={handleSaveEdit}>Save</button>
                  <button className='btn-cancel' onClick={handleCancelEdit}>Cancel</button>
                </div>
              ) : (
                <div className="btn-card">
                  <button className='btn-edit' onClick={() => handleEditProduct(product.id)}>Edit</button>
                  <button className='btn-delete' onClick={() => handleDeleteProduct(product.id)}>Delete</button>
                </div>
              )}
            </div>
          ))}
        </div>
      </div>
    </div>
  );
}

export default ProductList;
