import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import './styles.css'; // Import CSS file

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(true); // Mặc định hiển thị modal Add Product
  const [showEditProduct, setShowEditProduct] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });
  const [editingProduct, setEditingProduct] = useState({
    id: null,
    productName: '',
    price: '',
    linkImage: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (showEditProduct) {
      setEditingProduct({ ...editingProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowAddProduct(false); // Close the modal after adding product
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  // HandleEditProduct, handleSaveEdit, handleDeleteProduct functions remain unchanged...

  return (
    <div>
      {/* Modal for adding a new product */}
      <Modal
        size="lg"
        show={showAddProduct} // Use showAddProduct state to control modal visibility
        onHide={() => setShowAddProduct(false)} // Close modal when onHide event occurs
        aria-labelledby="contained-modal-title-vcenter"
        centered
      >
        <Modal.Body>
          <div className="container">
            <div className="form-container">
              <h2>Add Product</h2>
              <form onSubmit={handleSubmit}>
                <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
                <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
                <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
                <button type="submit">Add Product</button>
              </form>
            </div>
          </div>
        </Modal.Body>
      </Modal>
    </div>
  );
}

export default ProductList;
