import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Modal, Button, Form } from 'react-bootstrap';
import './styles.css'; // Import CSS file

function ProductList() {
  const [showAddProduct, setShowAddProduct] = useState(false);
  const [showEditProduct, setShowEditProduct] = useState(false);
  const [lgShow, setLgShow] = useState(false);
  const [products, setProducts] = useState([]);
  const [newProduct, setNewProduct] = useState({
    productName: '',
    price: '',
    linkImage: '',
  });
  const [editingProduct, setEditingProduct] = useState({
    id: null,
    productName: '',
    price: '',
    linkImage: '',
  });

  useEffect(() => {
    async function fetchProducts() {
      try {
        const response = await axios.get('http://localhost:8082/api/products');
        setProducts(response.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    }
    fetchProducts();
  }, []);

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (showEditProduct) {
      setEditingProduct({ ...editingProduct, [name]: value });
    } else {
      setNewProduct({ ...newProduct, [name]: value });
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const response = await axios.post('http://localhost:8082/api/products', newProduct);
      setProducts([...products, response.data]);
      setNewProduct({
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowAddProduct(false);
    } catch (error) {
      console.error('Error adding product:', error);
    }
  };

  const handleToggleAddProduct = () => {
    setShowAddProduct(!showAddProduct);
    setNewProduct({
      productName: '',
      price: '',
      linkImage: '',
    });
  };

  const handleEditProduct = (productId) => {
    const productToEdit = products.find(product => product.id === productId);
    setEditingProduct(productToEdit);
    setShowEditProduct(true);
  };

  const handleSaveEdit = async () => {
    try {
      await axios.put(`http://localhost:8082/api/products/${editingProduct.id}`, editingProduct);
      setEditingProduct({
        id: null,
        productName: '',
        price: '',
        linkImage: '',
      });
      setShowEditProduct(false);
    } catch (error) {
      console.error('Error editing product:', error);
    }
  };

  const handleDeleteProduct = async (productId) => {
    try {
      await axios.delete(`http://localhost:8082/api/products/${productId}`);
      setProducts(products.filter(product => product.id !== productId));
    } catch (error) {
      console.error('Error deleting product:', error);
    }
  };

  return (
    <div>
      <Button onClick={() => setLgShow(true)}>Large modal</Button>
      <Button className='btn-add' onClick={handleToggleAddProduct}>
        {showAddProduct ? 'Close Form' : 'Add Product'}
      </Button>

      <Modal
        size="lg"
        show={lgShow}
        onHide={() => setLgShow(false)}
        aria-labelledby="example-modal-sizes-title-lg"
      >
        <Modal.Body>
          {showAddProduct && (
            <div className="container">
              <div className="form-container">
                <h2>Add Product</h2>
                <form onSubmit={handleSubmit}>
                  <input type="text" name="productName" value={newProduct.productName} placeholder="Product Name" onChange={handleInputChange} />
                  <input type="number" name="price" value={newProduct.price} placeholder="Price" onChange={handleInputChange} />
                  <input type="text" name="linkImage" value={newProduct.linkImage} placeholder="Image URL" onChange={handleInputChange} />
                  <button type="submit">Add Product</button>
                </form>
              </div>
            </div>
          )}
        </Modal.Body>
      </Modal>

      <div className="container">
        <div className="table-container">
          {products.map((product, index) => (
            <div className="product-card" key={product.id}>
              <img src={product.linkImage} alt={product.productName} />
              <h3>{product.productName}</h3>
              <p>Price: ${product.price}</p>
              <div className='btn-card'>
                <button className='btn-edit' onClick={() => handleEditProduct(product.id)}>Edit</button>
                <button className='btn-delete' onClick={() => handleDeleteProduct(product.id)}>Delete</button>
              </div>
            </div>
          ))}
        </div>
      </div>

      <Modal show={showEditProduct} onHide={() => setShowEditProduct(false)}>
        <Modal.Header closeButton>
          <Modal.Title>Edit Product</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <Form>
            <Form.Group className="mb-3" controlId="editProductName">
              <Form.Label>Product Name</Form.Label>
              <Form.Control type="text" name="productName" value={editingProduct.productName} onChange={handleInputChange} autoFocus />
            </Form.Group>
            <Form.Group className="mb-3" controlId="editPrice">
              <Form.Label>Price</Form.Label>
              <Form.Control type="number" name="price" value={editingProduct.price} onChange={handleInputChange} />
            </Form.Group>
            <Form.Group className="mb-3" controlId="editLinkImage">
              <Form.Label>Image URL</Form.Label>
              <Form.Control type="text" name="linkImage" value={editingProduct.linkImage} onChange={handleInputChange} />
            </Form.Group>
          </Form>
        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={() => setShowEditProduct(false)}>Close</Button>
          <Button variant="primary" onClick={handleSaveEdit}>Save Changes</Button>
        </Modal.Footer>
      </Modal>
    </div>
  );
}

export default ProductList;
